import { State } from "../../types";
import { ReadShape } from "../../resource/types";
import { Schema, Denormalize } from "../../resource";
/**
 * Selects the Denormalize form from `state` cache.
 *
 * If `result` is not found, will attempt to generate it naturally
 * using params and schema. This increases cache hit rate for many
 * detail shapes.
 */
export default function useDenormalizedLegacy<Params extends Readonly<object>, S extends Schema>({ schema, getFetchKey }: Pick<ReadShape<S, Params>, 'schema' | 'getFetchKey'>, params: Params | null, state: State<any>): Denormalize<typeof schema> | null;
